import React from 'react';
import Head from 'next/head';
import { Form, Input, Checkbox, Button } from 'antd';
import styled from 'styled-components';

import AppLayout from '../components/AppLayout';
import useInput from '../hooks/useInput';

const ErrorMessage = styled.div`
  color: red
`;

const Signup = () => {
  const [ id, onChangeId ] = useInput('');
  const [ nickname, onChangeNickname] = useInput('');
  const [ password, onChangePassword ] = useInput('');
  const [ passwordCheck, setPasswordCheck ] = React.useState('');
  const [ passwordError, setPasswordError ] = React.useState(false);
  const onChangePasswordCheck = React.useCallback((e) => {
    setPasswordCheck(e.target.value);
    setPasswordError(e.target.value !== password);
  }, [password])
  const [ term, setTerm ] = React.useState('');
  const [ termError, setTermError ] = React.useState('');
  const onChangeTerm = React.useCallback((e) => {
    setTerm(e.target.checked);
    setTermError(false);
  }, [])

  const [test, setTest] = React.useState(false);
  const ch = (e) => {
    setTest(e.target.checked)
  }

  const onSubmit = React.useCallback(() => {
    if(password !== passwordCheck) {
      return setPasswordError(true);
    }
    if(!term) {
      return setTermError(true);
    }
    console.log(id, nickname, password)
  }, [password, passwordCheck, term])

  return (
    <>
    <AppLayout>
      <Head>
        <title>회원가입 | NodeBird</title>
      </Head>
      <Form onFinish={onSubmit}>
        <div>
          <label htmlFor="user-id">아이디</label>
          <br />
          <Input name="user-id" value={id} onChange={onChangeId} required />
        </div>
        <div>
          <label htmlFor="user-nickname">닉네임</label>
          <br />
          <Input name="user-nickname" value={nickname} onChange={onChangeNickname} required />
        </div>
        <div>
          <label htmlFor="user-password">비밀번호</label>
          <br />
          <Input name="user-password" value={password} onChange={onChangePassword} required />
        </div>
        <div>
          <label htmlFor="user-password-check">비밀번호 체크</label>
          <br />
          <Input 
            name="user-password-check"
            type="password"
            value={passwordCheck}
            required
            onChange={onChangePasswordCheck}
          />
          {passwordError && <ErrorMessage>비밀번호가 일치하지 않습니다.</ErrorMessage>}
        </div>
        <div>
          <Checkbox name="user-term" checked={term} onChange={onChangeTerm}>약관에 동의합니다.</Checkbox>
          {termError && <ErrorMessage>약관에 동의하셔야 합니다.</ErrorMessage>}
        </div>
        <div style={{ marginTop: 10 }}>
          <Button type="primary" htmlType="submit">가입하기</Button>
        </div>

        <input type="checkbox" onChange={ch} />{test ? '클릭' : '안클릭'}
      </Form>
      </AppLayout>
    </>
  );
};

export default Signup;
