import React from 'react';
import { Form, Input, Button } from 'antd';


export const PostForm = () => {
  const [ text, setText ] = React.useState('');
  const onChangeText = (e) => {
    setText(e.target.value);
  }
  const inpFileUploader = React.useRef('');
  const onClickFileUploader = React.useCallback(() => {
    inpFileUploader.current.click;
    console.log(inpFileUploader.current)
  }, [inpFileUploader.current])
  const onSubmit = React.useCallback(() => {

  }, []);

  return (
    <Form style={{ margin: '10px 0 20px' }} encType="multipart/form-data" onFinish={onSubmit}>
      <Input.TextArea 
        value={text} 
        onChange={onChangeText} 
        maxLength={140} 
        placeholder="내용을 입력하세요"
      />
      <div>
        <input type="file" multiple hidden />
        <Button onClick={onClickFileUploader} ref={inpFileUploader}>파일업로드</Button>
        <Button htmlType="submit" type="primary" style={{ float: 'right' }}>게시물 등록</Button>
      </div>
      <div></div>      
    </Form>
  );
}
