export const initialState = {
  mainPosts: [{
    id: 1,
    User: {
      id: 1,
      nickname: 'sunny',
    },
    content: '첫 번째 게시글 #해시태그',
    Images: [{
      src: 'https://bookthumb-phinf.pstatic.net/cover/137/13799585.jpg?update=20180726',
    }, {
      src: 'https://gimg.gilbut.co.kr/book/BN001958/rn_view_BN001958.jpg',
    }, {
      src: 'https://gimg.gilbut.co.kr/book/BN001998/rn_view_BN001998.jpg',
    }],
    Comments: [{
      User: {
        nickname: 'kim',
      },
      content: '우와 개정판이 나왔군요~',
    }, {
      User: {
        nickname: 'ohhh',
      },
      content: '와우우우우우우우~~~~',
    }]
  }],
  imagePaths: [],
  postAdded: false,
}

const ADD_POST = "ADD_POST";
export const addPost = {
  type: ADD_POST,
}
const dummyPost = {
  id: 2, 
  content: '더미테이터입니다.',
  User: {
    id: 1,
    nickname: 'sunny'
  },
  Images: [],
  Comments: [],
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST:
      return {
        ...state,
        mainPosts: [dummyPost, ...state.mainPosts],
        postAdded: true
      }
    default:
      return state;
  }
}

export default reducer;
