import { HYDRATE } from 'next-redux-wrapper';

const initialState = {
  user: {
    isLoggedIn: false,
    user: null,
    signUpData: {},
    loginData: {}
  },
  post: {
    mainPost: []
  }
}

// action creator
export const loginAction = (data) => {
  return {
    type: 'LOG_IN',
    data
  }
}

// action creator
export const logoutAction = (data) => {
  return {
    type: 'LOG_OUT',
    data
  }
}

// action creator
// const changeNickname = (data) =>  {
//   return {
//     type: 'CHANGE_NICKNAME',
//     data
//   }
// }

// (이전상태, 액션) => return 다음상태
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case HYDRATE: 
      return { ...state, ...action.payload }
    case 'LOG_IN':
      return {
        ...state,
        user: {
          ...state.user,
          isLoggedIn: true,
          user: action.data,
        }
      }
    case 'LOG_OUT':
      return {
        ...state,
        user: {
          ...state.user,
          isLoggedIn: false,
          user: null,
        }
      }
    default:
      return state;
  }
}

export default rootReducer;
